mapper.update:
update SYS_RES set 
	sn=#{sn,jdbcType=NUMERIC}
where  
 resId = #{resId}

mapper.select:
SELECT FROM SYS_RES
order by ${orderField} ${orderSeq}
ORDER BY sn

SELECT  FROM SYS_RES 
WHERE  parentId=#{parentId} 
and resid in (
	select a.resid from sys_role_res a where a.roleid in (
		select t.roleid from sys_user_role t where t.userid=${userid}
	)
)
ORDER BY sn

SELECT 
FROM SYS_RES WHERE  systemId=#{systemId}
ORDER BY sn
